mapper.insert:
insert into quanxian (qx_id, qx_name, qx_remarks
  )
values (#{qxId,jdbcType=INTEGER}, #{qxName,jdbcType=VARCHAR}, #{qxRemarks,jdbcType=VARCHAR}
  )

mapper.update:
update quanxian
set qx_name = #{qxName,jdbcType=VARCHAR},
  qx_remarks = #{qxRemarks,jdbcType=VARCHAR}
where qx_id = #{qxId,jdbcType=INTEGER}

mapper.select:
select qx_id, qx_name, qx_remarks
from quanxian
where qx_id = #{qxId,jdbcType=INTEGER}
